    public boolean addNew(Game game) {
        SQLiteDatabase db = getWritableDatabase();
        db.beginTransaction();
        try {
            ContentValues values = new ContentValues();
            values.put("title", game.getTitle());
            values.put("platform", game.getPlatform());
            values.put("developer", game.getDeveloper());
            values.put("publisher", game.getPublisher());
            values.put("rating", game.getRating());
            values.put("releasedate", game.getreleaseDate());
            values.put("nuevo", game.getNuevo());
            values.put("image", game.getImage());

            // Notice how we haven't specified the primary key. SQLite auto increments the primary key column.
            db.insertOrThrow("Game", null, values);
            db.setTransactionSuccessful();
        } catch (Exception e) {
            Log.d(TAG, "Error while trying to add post to database");
            return false;
        } finally {
            db.endTransaction();
            db.close();
            return true;
        }
    }

    public ArrayList<Game> getAllGames() {
        ArrayList<Game> list = new ArrayList<Game>();
        try {
            SQLiteDatabase db = getReadableDatabase();

            String[] campos = new String[] {"id", "title", "platform", "image"};
            Cursor c = db.query("Game", campos, null, null, null, null, null);

            //Nos aseguramos de que existe al menos un registro
            if (c.moveToFirst()) {
                //Recorremos el cursor hasta que no haya más registros
                do {
                    Game g = new Game();
                    g.setId(c.getInt(c.getColumnIndex("id")));
                    g.setTitle(c.getString(c.getColumnIndex("title")));
                    g.setPlatform(c.getString(c.getColumnIndex("platform")));
                    g.setImage(c.getString(c.getColumnIndex("image")));
                    list.add(g);
                } while(c.moveToNext());
            }
            db.close();
        }
        catch(Exception e){

        }
        return list;
    }

    public ArrayList<Game> getGamesByCriteria(String title, String platform, String date) {
        ArrayList<Game> list = new ArrayList<Game>();
        SQLiteDatabase db = getWritableDatabase();

        String[] args = new String[] {title, platform, date};
        Cursor c = db.rawQuery("SELECT id, title, platform, image FROM Game WHERE title LIKE ? AND platform LIKE ? AND releaseDate LIKE ?", args);

        //Nos aseguramos de que existe al menos un registro
        if (c.moveToFirst()) {
            do {
                Game g = new Game();
                g.setId(c.getInt(c.getColumnIndex("id")));
                g.setTitle(c.getString(c.getColumnIndex("title")));
                g.setPlatform(c.getString(c.getColumnIndex("platform")));
                g.setImage(c.getString(c.getColumnIndex("image")));
                list.add(g);
            } while(c.moveToNext());
        }
        db.close();
        return list;
    }

    public Game getGameById(int id) {
        Game g = new Game();
        SQLiteDatabase db = getReadableDatabase();

        String[] args = new String[] {id+""};

        Cursor c = db.rawQuery("SELECT title, platform, developer, publisher, rating, releasedate, nuevo, image FROM Game WHERE id=?", args);

        //Nos aseguramos de que existe al menos un registro
        if (c.moveToFirst()) {
            do {
                g.setId(id);
                g.setTitle(c.getString(c.getColumnIndex("title")));
                g.setDeveloper(c.getString(c.getColumnIndex("developer")));
                g.setPublisher(c.getString(c.getColumnIndex("publisher")));
                g.setRating(c.getString(c.getColumnIndex("rating")));
                g.setPlatform(c.getString(c.getColumnIndex("platform")));
                g.setReleaseDate(c.getString(c.getColumnIndex("releasedate")));
                g.setNuevo(Boolean.parseBoolean(c.getString(c.getColumnIndex("nuevo"))));
                g.setImage(c.getString(c.getColumnIndex("image")));
            } while(c.moveToNext());
        }
        db.close();
        return g;
    }

    public boolean updateGame(Game game) {
        SQLiteDatabase db = getWritableDatabase();
        String where = "id=?";
        String[] whereArgs = new String[] {String.valueOf(game.getId())};
        db.beginTransaction();
        try {
            ContentValues values = new ContentValues();
            values.put("title", game.getTitle());
            values.put("platform", game.getPlatform());
            values.put("developer", game.getDeveloper());
            values.put("publisher", game.getPublisher());
            values.put("releasedate", game.getreleaseDate());
            values.put("rating", game.getRating());
            values.put("nuevo", game.getNuevo());
            values.put("image", game.getImage());

            db.update("Game", values, where, whereArgs);
            db.setTransactionSuccessful();
        } catch (Exception e) {
            Log.d(TAG, "Error al actualizar juego en la base de datos");
            return false;
        } finally {
            db.endTransaction();
            db.close();
            return true;
        }
    }

    public boolean deleteGame(int id) {
        SQLiteDatabase db = getWritableDatabase();

        String whereClause = "id=?";
        String[] whereArgs = new String[] { String.valueOf(id) };

        return db.delete("Game", whereClause, whereArgs) > 0;
    }

    private void insertRecordOnBoot(SQLiteDatabase db, Game game){
        ContentValues values = new ContentValues();
        values.put("title", game.getTitle());
        values.put("platform", game.getPlatform());
        values.put("developer", game.getDeveloper());
        values.put("publisher", game.getPublisher());
        values.put("rating", game.getRating());
        values.put("releasedate", game.getreleaseDate());
        values.put("nuevo", game.getNuevo());
        values.put("image", game.getImage());
        db.insertOrThrow("Game", null, values);
    }

    public static ArrayList<String> getPlatformsList(boolean addPercentage) {
        ArrayList<String> platforms = new ArrayList<String>();
        if(addPercentage) platforms.add("%");
        platforms.add("PC");
        platforms.add("PS4");
        platforms.add("Xbox One");
        platforms.add("Switch");
        return platforms;
    }
